import React, { useEffect, useState } from "react";
import { useAuth0 } from "@auth0/auth0-react";
import { useNavigate } from "react-router-dom";
import MenuBar from "./MenuBar";
import './userprofile.css';

const Profile = () => {
    const { user, isAuthenticated, isLoading, getAccessTokenSilently } = useAuth0();
    const navigate = useNavigate();
    const [likedGamesCount, setLikedGamesCount] = useState(0);

    const onClose = () => {
        navigate('/')
    };

    const fetchLikedGamesCount = async () => {
        try {
            const token = await getAccessTokenSilently();
            const response = await fetch('/api/liked-games/count', {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });
            if (response.ok) {
                const { count } = await response.json();
                setLikedGamesCount(count);
            } else {
                console.error('Failed to fetch liked games count');
            }
        } catch (error) {
            console.error('Error while fetching liked games count', error);
        }
    };

    useEffect(() => {
        if (isAuthenticated) {
            fetchLikedGamesCount();
        }
    }, [isAuthenticated]);

    if (isLoading) {
    return <div>Loading ...</div>;
    }

    if (!isAuthenticated) {
        return (
            <div className="profile-page">
                <MenuBar className="profile-menu" />
                <h3>Please login to view your profile.</h3>
                <div className="back-button-LG">
                    <button className="rainbow-button" onClick={onClose}>Back</button>
                </div>
            </div>
        );
    }

    return (
    isAuthenticated && (
        <div className="profile-page">
            <MenuBar />
            <div className="user-info">
                <img src={user.picture} alt={user.name} />
                <h2>{user.name}</h2>
                <p>{user.email}</p>
                <p>Liked Games: {likedGamesCount}</p>
            </div>
            <div className="back-button-LG">
                <button className="rainbow-button" onClick={onClose}>Back</button>
            </div>  
        </div>
    )
    );
};

export default Profile;
