import React from "react";
import { useNavigate } from 'react-router-dom';
import { Button } from '@mui/material';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import { Card, CardMedia, CardContent, Typography } from '@mui/material';

function LikedGamesPage({ likedGames }) {

    const theme = createTheme({
        palette: {
            primary: {
                main: '#558b81',
            },
            secondary: {
                main: '#497d3b',
            },
            back: {
                main: '#a697af',
                contrastText: '#3c224d',
                dark: '#9c56c7',
            },
            card: {
                main: '#CCCCCC',
            },
        },
      });

    const styles = {
        media: {
            height: '375px',
            width: 'auto',
            borderRadius: '25px',
            marginTop: '25px',
            filter: 'drop-shadow(10px 10px 1px grey)'
        },
    };

    const navigate = useNavigate();

    const onClose = () => {
        navigate('/')
    };
    if (likedGames.length === 0) {
        return (
            <ThemeProvider theme={theme}>
                <div className="liked-games-page">
                    <h2 className="liked-games-title">Liked Games</h2>
                    <p>No liked games found.</p>
                    <div className="back-button-LG">
                        <Button variant="contained" color="back" onClick={onClose}>Back</Button>
                    </div>
                </div>
            </ThemeProvider>
        );
    }
    return (
        <ThemeProvider theme={theme}>
            <div className="liked-games-page">
                <h2 className="liked-games-title">Liked Games</h2>
                <ul>
                    {likedGames.map((game, index) => (
                        <li key={index}>
                            <Card raised sx={{ width: '30vw', height: '40vh', backgroundColor: theme.palette.card.main, zIndex: 1, marginTop: index === 0 ? '150px' : 0, marginBottom: index === likedGames.length - 1 ? '100px' : 0, border: '1px solid white', borderRadius: '25px' }}>
                                <div style={{ display: 'flex', justifyContent: 'center' }} >
                                    <CardMedia component="img" style={styles.media} alt={game.title} image={game.cover} />
                                </div>                                
                                <CardContent>
                                    <Typography gutterBottom style={{ textAlign: 'center', fontFamily: 'sans-serif' }} variant="h4" padding="5%" >{game.title}</Typography>
                                </CardContent>
                            </Card>
                        </li>
                    ))}
                </ul>
                <div className="back-button-LG">
                    <Button variant="contained" color="back" onClick={onClose}>Back</Button>
                </div>    
            </div>
        </ThemeProvider>
    );
}

export default LikedGamesPage;
