import React, { useEffect, useState } from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import reportWebVitals from './reportWebVitals';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import LikedGamesPage from './components/LikedGames';
import Login from './components/Login';
import { Auth0Provider, useAuth0 } from '@auth0/auth0-react';

function Index() {
  const auth0Config = {
    domain: 'dev-0y2p15ofhuro1lwb.us.auth0.com',
    clientID: 'F36er9YNgRHzPg1dvIg1tsXNQU7nku40',
  };
  const [likedGames, setLikedGames] = useState([]);
  const { isAuthenticated } = useAuth0();
  const [showLikedGamesPage, setShowLikedGamesPage] = useState(false);

  const handleLikedGamesClose = () => {
    setShowLikedGamesPage(false);
    window.location.href = '/';
  };

  useEffect(() => {
    if(!isAuthenticated && window.location.pathname !== '/login') {
      window.location.href = '/login';
    }
  }, [isAuthenticated]);

  return (
    <React.StrictMode>
      <Router>
        <Auth0Provider authorizationParams={{
          redirectUri: window.location.origin
        }} 
        {...auth0Config}>
          <Routes>
            <Route path="/login" element={<Login />} />
            <Route exact path="/" element={<App likedGames={likedGames} setLikedGames={setLikedGames} setShowLikedGamesPage={handleLikedGamesClose} />} />
            <Route path='/liked-games' element={<LikedGamesPage likedGames={likedGames} panelItems={['Liked Games']} onClose={handleLikedGamesClose} />} />
          </Routes>
        </Auth0Provider>
      </Router>
    </React.StrictMode>
  );
}
const root = document.getElementById('root');
ReactDOM.render(<Index />, root);
reportWebVitals();
