import React, { useEffect, useState } from "react";
import { useAuth0, useLogout } from "@auth0/auth0-react";
import { useNavigate } from "react-router-dom";
import './login.css';

const Login = () => {

    const { loginWithRedirect, isAuthenticated, isLoading, logout } = useAuth0();
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [errorMessage, setErrorMessage] = useState("");
    const navigate = useNavigate();
    const handleLogout = useLogout();

    const handleLogin = async () => {
        if(username && password) {
            try {
                await loginWithRedirect({
                    screen_hint: "login",
                    login_hint: username,
                    password,
                });
            } catch(error) {
                setErrorMessage("Invalid username or password.");
            }            
        } else {
            setErrorMessage("Please provide a username and password.");
        }
    };

    const handleSignUp = async () => {
        try {
            await loginWithRedirect({
                screen_hint: "signup",
                login_hint: username,
                password,
            });
        } catch (error) {
            setErrorMessage("Error signing up.");
        }
    };

    const handleLogoutClick = () => {
        handleLogout({ returnTo: window.location.origin });
      };

    useEffect(() => {
        if(isAuthenticated) {
            navigate("/");
        }
    }, [isAuthenticated, navigate]);

    if(isLoading) {
        return <div>Loading...</div>
    }

    return (
    <div className="login-page-container">
        <h2>Login</h2>
        {errorMessage && <p style={{ color: 'red', fontWeight: 650 }}>{errorMessage}</p>}
        <form className="form-container">
            <label className="label">
                Username: {" "} <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} />
            </label>
            <label className="label">
                Password: {" "} <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
            </label>
            <button className="login-button" type="button" onClick={handleLogin}>Login</button>
            <button className="login-button" type="button" onClick={handleSignUp}>Sign Up</button>
        </form>
    </div>
    );
};

export default Login;
