import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBars, faL } from '@fortawesome/free-solid-svg-icons';
import logo from './ProjectMARSLogo.png';
import './App.css';

function App() {
  const [logoVisible, setLogoVisible] = useState(true);
  const [welcomeText, setWelcomeText] = useState('Welcome to ProjectMARS');
  const [buttonClicked, setButtonClicked] = useState(false);
  const [menuOpen, setMenuOpen] = useState(false);

  const handleButtonClick = () => {
    setLogoVisible(true);
    setWelcomeText('You should not have come here.');
    setButtonClicked(true);
  };
  const handleBackClick = () => {
    setLogoVisible(true);
    setWelcomeText('Welcome to ProjectMARS');
    setButtonClicked(false);
  };

  return (
    <div className="App">
      <header className="App-header">
        <div className='menu'>
          <FontAwesomeIcon icon={faBars}/>
        </div>
        <div className='logo-container'>
          {logoVisible && <img src={logo} className='App-logo' alt="logo"/>}
        </div>
        <p>{welcomeText}</p>
        {!buttonClicked && (
          <button onClick={handleButtonClick}>Click me</button>
        )}
        {buttonClicked && (
          <button onClick={handleBackClick}>Back</button>
        )}
      </header>
    </div>
  );
}

export default App;
