import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBars, faL } from '@fortawesome/free-solid-svg-icons';
import logo from './ProjectMARSLogo.png';
import './App.css';

function App() {
  const [logoVisible, setLogoVisible] = useState(true);
  const [welcomeText, setWelcomeText] = useState('Welcome to ProjectMARS');
  const [buttonClicked, setButtonClicked] = useState(false);
  const [menuOpen, setMenuOpen] = useState(false);
  const [panelItems, setPanelItems] = useState([
    'Item 1',
    'Item 2',
    'Item 3'
  ]);

  const handleMenuClick = () => {
    setMenuOpen(!menuOpen);
  };
  const handlePanelClick = (item) => {
    console.log('Clicked item: ', item)
  };
  const handleButtonClick = () => {
    setWelcomeText('Turn back')
  };
  const handleBackClick = () => {
    setLogoVisible(true);
    setWelcomeText('Welcome to ProjectMARS');
    setButtonClicked(false);
  };

  return (
    <div className="App">
      <div className='menu-bar'>
          <div className={`menu ${menuOpen ? 'open' : ''}`} onClick={handleMenuClick}>
            <FontAwesomeIcon icon={faBars}/>
            {menuOpen && (
              <div className="menu-panel">
                <ul>
                  {panelItems.map((item, index) => (
                    <li key={index} onClick={() => handlePanelClick(item)}>
                      {item}
                    </li>
                  ))}
                </ul>
              </div>
            )}
          </div>
        </div>
        <header className="App-header">
        <div className='logo-container'>
          {logoVisible && <img src={logo} className='App-logo' alt="logo"/>}
        </div>
        <p>{welcomeText}</p>
        {!buttonClicked && (
          <button onClick={handlePanelClick}>Click me</button>
        )}
        {buttonClicked && (
          <button onClick={handleBackClick}>Back</button>
        )}
      </header>
    </div>
  );
}

export default App;
