import React from "react";
import { useNavigate } from 'react-router-dom';
import { Button } from '@mui/material';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import { Card, CardMedia, CardContent, Typography } from '@mui/material';

function LikedGamesPage({ likedGames }) {

    const theme = createTheme({
        palette: {
            primary: {
                main: '#558b81',
            },
            secondary: {
                main: '#497d3b',
            },
            back: {
                main: '#a697af',
                contrastText: '#3c224d',
                dark: '#9c56c7',
            },
            card: {
                main: '#CCCCCC',
            },
        },
      });

    const navigate = useNavigate();

    const onClose = () => {
        navigate('/')
    };

    if (likedGames.length === 0) {
        return (
            <ThemeProvider theme={theme}>
                <div className="liked-games-page">
                    <h2 className="liked-games-title">Liked Games</h2>
                    <p>No liked games found.</p>
                    <div className="back-button-LG">
                        <Button variant="contained" color="back" onClick={onClose}>Back</Button>
                    </div>
                </div>
            </ThemeProvider>
        );
    }
    return (
        <ThemeProvider theme={theme}>
            <div className="liked-games-page">
                <h2 className="liked-games-title">Liked Games</h2>
                <ul>
                    {likedGames.map((game, index) => (
                        <li key={index}>
                            <Card sx={{ width: 500, height: 400, backgroundColor: theme.palette.card.main, display: "flex", flexDirection: 'column' }}>
                                <CardMedia component="img" alt={game.title} image={game.cover} sx={{ height: '60%', objectFit: 'cover', flexGrow: 1 }} />                                
                                <CardContent>
                                    <Typography variant="h4" padding="5%" >{game.title}</Typography>
                                </CardContent>
                            </Card>
                        </li>
                    ))}
                </ul>
                <div className="back-button-LG">
                    <Button variant="contained" color="back" onClick={onClose}>Back</Button>
                </div>    
            </div>
        </ThemeProvider>
    );
}

export default LikedGamesPage;
